mapper.select:
select u.fstatus , u.fuserloginname , u.fuserpassword , u.fuserpasswordsalt , u.fusermale , u.fcellphonenumber , u.flogincount , u.flastlogintime , u.fweixinopenid , u.fdefaultloginorgid , u.fname , u.fdescription , u.fcreateuserid , u.fcreatedate , u.flastupdateuserid , u.flastupdatedate , u.fid , u.fversion , r.fid as forgrelationid from (select * from t_common_user u
order by hex(u.fname) desc, u.fid
limit #{pageDevice.startRowIndexOfCurrentPage},#{pageDevice.recordsPerPage}
) u left join t_common_userorgrelation r on u.fid=r.fuserid
